"#include <stdio.h>\n#include <windows.h>\n\nmain ()\n{\n  Sleep(10);\n  printf(\"Which process do you want to do? [1/2/3/4]\");\n  int which = getchar();\n\n if(which == \'1\' or which == \'2\' or which == \'3\' or which == \'4\')\n {\n  Sleep(50);\n  printf(\"Do you want to start processing? [y/n]\");\n }\n\n  while(which == \'1\' or which == \'2\' or which == \'3\' or which == \'4\')\n  {\n    int yn = getchar();\n\n    if(yn == \'y\')\n    {\n      if(which == \'1\')\n      {\n        int r;\n\n        for(int i = 0;i < 1000;i++)\n        {\n          for(int i = 0;i < 200;i++)\n          {\n            r = rand() % 2 ;\n            printf(\"%d\",r);\n          }\n          Sleep(10);\n        }\n      }\n\n      else if(which == \'2\')\n      {\n        int r;\n        for(int i = 0;i < 50;i++)\n        {\n          printf(\"\\nProgress Now \");\n          int ii = 0;\n          while(ii < 20)\n          {\n            r = rand() % 8 ;\n            if(r == 1)\n            {\n              printf(\"-\");\n              ii++;\n            }\n            Sleep(10);\n          }\n          Sleep(10);\n        }\n      }\n\n      else if(which == \'3\')\n      {\n        int r;\n        for(int i = 0;i < 50;i++)\n        {\n          printf(\"\\nProgress Now .......\");\n          char srt[8] = {\'-\',\'\\\',\'|\',\'/\',\'-\',\'\\\',\'|\',\'/\'};\n          for(int ii = 0;ii < 8;ii++)\n          {\n            r = rand() % 20 + 1;\n            printf(\"\\rProgress Now .......%c\",srt[ii]);\n            Sleep(r*10);\n          }\n        }\n      }\n\n      else if (which == \'4\')\n      {\n        int r;\n        for(int i = 0;i < 50;i++)\n        {\n          printf(\"\\nProgress Now .......\");\n          for(int ii = 0;ii < 101;ii++)\n          {\n            printf(\"\\rProgress Now .......%d%%\",ii);\n            r = rand() % 3 + 1;\n            Sleep(r*5);\n          }\n        }\n      }\n\n      printf(\"\\nProcessing is complete\");\n      which = 1;\n    }\n    else if(yn == \'n\')\n    {\n      which = 1;\n    }\n    else if(which == \'1\' or which == \'2\' or which == \'3\' or which == \'4\')\n    {\n      printf(\"\\rDo you want to start processing? [y/n]\");\n    }\n  }\n  Sleep(1000);\n}";
